checkForward :: String -> Count
checkForward [] = 0
checkForward line = case line of 
  'X':'M':'A':'S':rest -> 1 + checkForward rest
  _:rest               -> checkForward rest

checkBackwards :: String -> Count
checkBackwards = checkForward . reverse

calculate :: [[Char]] -> Count
calculate lines = 
  (sum $ map checkForward lines) +
  (sum $ map checkBackwards lines) +
  (sum $ map checkForward (transpose lines)) +
  (sum $ map checkBackwards (transpose lines))+

  (sum $ map checkForward (diagonals lines)) +
  (sum $ map checkBackwards (diagonals lines))+
  (sum $ map checkForward (diagonals $ invert lines)) +
  (sum $ map checkBackwards (diagonals $ invert lines))
  where invert grid = map reverse grid
